dict.string:
echo "<title>Ruby doc for ${TM_SELECTED_TEXT:=$TM_CURRENT_WORD}</title>"
ri -Tf html "${TM_SELECTED_TEXT:=$TM_CURRENT_WORD}"|{
	read MSG
	if [[ $MSG == "More than one method matched your request. You can refine" ]]; then
		read MSG; read MSG
		cat <<'EOF'
		<script>
		function ri (arg) {
			document.getElementById("result").innerText = "";

			function output (str) {
			   document.getElementById("result").innerText += str;
			}

			var cmd = "ri -Tf plain '" + arg + "'"
			var myCommand = TextMate.system(cmd, function (task) { });
			myCommand.onreadoutput = output;
		}
		</script>
		<h1>Multiple matches</h1>
		<ul>
EOF
		perl -pe 's/(.+?)(, |$)/<li><a href="javascript:ri(&quot;$1&quot;)">$1<\/a><\/li>\n/g'
		echo "</ul><hr><pre id="result"></pre>"
	else
		echo "$MSG"; cat
	fi
}
