dict.string:
#!/usr/bin/env ruby

class String
  def escape(char)
    gsub(/\\.|#{Regexp.quote(char)}/) { |match| match == char ? "\\#{char}" : match }
  end

  def unescape(char)
    gsub(/\\./) { |match| match == "\\#{char}" ? char : match }
  end
end

print case str = STDIN.read
  when /\A"(.*)"\z/m: "%{" + $1.unescape('"').escape("}") + "}"
  when /\A%\{(.*)\}\z/m: "'" + $1.unescape("}").escape("'") + "'"
  when /\A'(.*)'\z/m: "%q{" + $1.unescape("'").escape("}") + "}"
  when /\A%q\{(.*)\}\z/m: '"' + $1.unescape("}").escape('"') + '"'
  else str
end
